import React, { useEffect, useState} from 'react';
import { getDatabase, ref, get } from 'firebase/database';
import { useParams } from 'react-router-dom';
import { getStorage, ref as storageRef, getDownloadURL } from 'firebase/storage';


function ListingDetails() {
  const [listingData, setListingData] = useState();
  const [listingImage, setListingImage] = useState()
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      const database = getDatabase();
      const snapshot = await get(ref(database, `supplyListings/${id}`));

      if (snapshot.exists()) {
        const data = snapshot.val();
        setApartmentData(data);

        const storage = getStorage();
        const imageRef = storageRef(storage, `images/${id}`);

        try {
          const imageUrl = await getDownloadURL(imageRef);
          setApartmentImage(imageUrl);
        } catch (error) {
          console.error('Error fetching image:', error.message);
        }
      } else {
        console.log('No data available');
      }
    };

    fetchData();
  }, [id]);


  return (
    <div className="listing-container">
      {listingData ? (
        <>
          <h3>{listingData.name}</h3>
          <p>Condition: {listingData.condition}</p>
          <p>Price: ${listingData.price}</p>
          <p>Note from seller: {listingData.description}</p>
          <button className="contact-seller-button" onClick={contactOwner}>
            Contact Seller
          </button>
        </>
      ) : (
        <p>Error Retrieving Data!</p>
      )}
    </div>
  );
}

export default ListingDetails;
