import React, { useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import profiles from './profileData';
import './style.css'; // Import the CSS file

const ProfileForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    image: null,
    major: '',
    grade: '',
    bio: ''
  });

  const [selectedProfiles, setSelectedProfiles] = useState([]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: name === 'image' ? files[0] : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    try {
      localStorage.setItem('profileData', JSON.stringify(formData));
      alert('Profile saved successfully!');
      setFormData({
        name: '',
        image: null,
        major: '',
        grade: '',
        bio: ''
      });

      // Filter profiles based on major and grade
      const filtered = profiles.filter(profile =>
        profile.major === formData.major || profile.grade === formData.grade
      );
      setSelectedProfiles(filtered);
    } catch (error) {
      console.error('Error saving profile:', error);
      alert('Error saving profile. Please try again later.');
    }
  };

  const toggleProfileSelection = (profile) => {
    const isSelected = selectedProfiles.some(p => p.id === profile.id);
    if (isSelected) {
      // If the profile is already selected, remove it from the list
      const updatedProfiles = selectedProfiles.filter(p => p.id !== profile.id);
      setSelectedProfiles(updatedProfiles);
    } else {
      // If the profile is not selected, add it to the list
      setSelectedProfiles([...selectedProfiles, profile]);
    }
  };

  return (
    <div>
      <Header />
      <div className="profile-container">
        <h1>Create Your Profile</h1>
        <form className="profile-form" onSubmit={handleSubmit}>
          {/* Form fields here */}
          <button type="submit">Save Profile</button>
        </form>
      </div>
      <div>
        {/* Display filtered profiles */}
        {profiles.map(profile => (
          <div
            key={profile.id}
            className={`card ${selectedProfiles.some(p => p.id === profile.id) ? 'selected' : ''}`}
            onClick={() => toggleProfileSelection(profile)}
          >
            <div className="card-body">
              <h5 className="card-title">{profile.name}</h5>
              <p className="card-text">Major: {profile.major}</p>
              <p className="card-text">Grade: {profile.grade}</p>
              <p className="card-text">Bio: {profile.bio}</p>
            </div>
          </div>
        ))}
      </div>
      <Footer />
    </div>
  );
};

export default ProfileForm;
