import Header from './Header'; 
import Footer from './Footer';
import { getDatabase, ref, push } from 'firebase/database';
import { getStorage, uploadBytes } from '@firebase/storage';
import React, { useState } from 'react';

const Sell = () => {
  const database = getDatabase();

  const saveSupplyListingToDatabase = (supplyData) => {
    push(ref(database, 'supplyListings'), supplyData)
      .then(() => {
        console.log('Listing data saved successfully');
      })
      .catch((error) => {
        console.error('Error saving listing data:', error);
      });
  };

  const [formData, setFormData] = useState({
    supplyType: '', // Updated field name
    condition: '',
    description: '',
    price: '',
    email: '',
    image: null, // Added image field
  });

  const storage = getStorage();

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file) {
      const storageRef = ref(storage, 'images/' + file.name);

      uploadBytes(storageRef, file)
        .then((snapshot) => {
          console.log('Uploaded file!', snapshot);
          const imageUrl = snapshot.metadata.fullPath;

          setFormData((prevData) => ({
            ...prevData,
            image: imageUrl,
          }));
        })
        .catch((error) => {
          console.error('Error uploading file:', error);
        });
    } else {
      console.error('No file selected.');
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    saveSupplyListingToDatabase(formData);
    alert('Supply Listing Submitted!');
    // Optionally, reset the form after successful submission
    setFormData({
      supplyType: '',
      condition: '',
      description: '',
      price: '',
      email: '',
      image: null,
    });
  };

  return (
    <div>
      <Header />
      <div className="listing-container">
        <section id="add-listing">
          <h1>Add School Supply Listing</h1>
          <div className="listing-form">
            <form onSubmit={handleSubmit}>
              {/* ... (rest of your form) */}
            </form>
          </div>
        </section>
      </div>
      <Footer />
    </div>
  );
};

export default Sell;
