import React, { useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import profiles from './profileData';

const ProfileForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    image: null,
    major: '',
    grade: '',
    bio: ''
  });

  const [filteredProfiles, setFilteredProfiles] = useState([]);
  const [selectedProfiles, setSelectedProfiles] = useState([]);
  const [contactBook, setContactBook] = useState([]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: name === 'image' ? files[0] : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    try {
      localStorage.setItem('profileData', JSON.stringify(formData));
      alert('Profile saved successfully!');
      setFormData({
        name: '',
        image: null,
        major: '',
        grade: '',
        bio: ''
      });

      // Filter profiles based on major and grade
      const filtered = profiles.filter(profile =>
        profile.major === formData.major || profile.grade === formData.grade
      );
      setFilteredProfiles(filtered);
    } catch (error) {
      console.error('Error saving profile:', error);
      alert('Error saving profile. Please try again later.');
    }
  };

  const handleProfileClick = (profile) => {
    if (!selectedProfiles.includes(profile)) {
      setSelectedProfiles(prevSelected => [...prevSelected, profile]);
    } else {
      setSelectedProfiles(prevSelected => prevSelected.filter(p => p.id !== profile.id));
    }
  };

  const isProfileSelected = (profile) => {
    return selectedProfiles.includes(profile);
  };

  const addToContactBook = () => {
    setContactBook(prevContacts => [...prevContacts, ...selectedProfiles]);
    setSelectedProfiles([]);
  };

  return (
    <div>
      <Header />
      <div className="profile-container">
        <h1>Create Your Profile</h1>
        <form className="profile-form" onSubmit={handleSubmit}>
          {/* Input fields for profile creation */}
        </form>
      </div>
      <div>
        {/* Display filtered profiles */}
        {filteredProfiles.length > 0 && (
          <div className='allCards'>
            {filteredProfiles.map(profile => (
              <div
                key={profile.id}
                className={`card ${isProfileSelected(profile) ? 'selected' : ''}`}
                onClick={() => handleProfileClick(profile)}
              >
                <div className="card-body">
                  <h5 className="card-title">{profile.name}</h5>
                  <p className="card-text">Major: {profile.major}</p>
                  <p className="card-text">Grade: {profile.grade}</p>
                  <p className="card-text">Bio: {profile.bio}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
      <div>
        <button onClick={addToContactBook}>Add Selected Profiles to Contact Book</button>
        <h2>Contact Book</h2>
        <ul>
          {contactBook.map(profile => (
            <li key={profile.id}>{profile.name}</li>
          ))}
        </ul>
      </div>
      <Footer />
    </div>
  );
};

export default ProfileForm;
