import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getDatabase, ref, get } from 'firebase/database';
import { getStorage, ref as storageRef, getDownloadURL } from 'firebase/storage';


const FullListing = () => {
  const [listingData, setListingData] = useState(null);
  const [litingImage, setListingImage] = useState(null);

  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      const database = getDatabase();
      const snapshot = await get(ref(database, 'supplyListings'));
      const data = [];
      if (snapshot.exists()) {
        snapshot.forEach((childSnapshot) => {
          data.push({ id: childSnapshot.key, ...childSnapshot.val() });
        });
      }
      setListings(data);
    };
    fetchData();
  }, [id]); 


  const contactOwner = () => {
    const ownerEmail = listingData.email;
    window.location.href = `mailto:${ownerEmail}`;
  };

  return (
    <div className="listing-info">
      <div>
      <p className="listingname">{apartmentData.address}</p>
      </div>
      <div>
        <img src={apartmentImage || getImage()} alt="Apartment Picture" />
      </div>
      <div>
        <h3>Bedrooms:</h3>
        <p>{apartmentData.bedrooms}</p>
      </div>
      <div>
        <h3>Bathrooms:</h3>
        <p>{apartmentData.bathrooms}</p>
      </div>
      <div>
        <h3>Rent:</h3>
        <p> ${apartmentData.price} per month</p>
      </div>

      <div>
        <h3>Duration:</h3>
        <p> {formatDate(apartmentData.start_date)} - {formatDate(apartmentData.end_date)} </p>
      </div>
      <div>
        <h3>Pets Allowed: </h3>
        <p>{apartmentData.pets ? 'Yes' : 'No'}</p>
      </div>
      <div>
        <h3>Number of Current Roommates:</h3>
        <p>{apartmentData.roommates > 0 ? apartmentData.roommates : 'None'}</p>
      </div>
      <div>
        {}
        <button className="contact-owner-button" onClick={contactOwner}>
          Contact Owner
        </button>
      </div>
    </div>
  );
};

export default ApartmentDetail;
