import React, { useState } from 'react';

const BlogPostForm = ({ onAddPost }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title || !content) return; // Validation, both title and content are required

    // Create a new post object
    const newPost = {
      title: title,
      content: content,
      timestamp: new Date().toISOString(), // You can add timestamp or any other data you need
    };

    // Pass the new post object to the parent component
    onAddPost(newPost);

    // Clear the form fields
    setTitle('');
    setContent('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Content:</label>
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
        ></textarea>
      </div>
      <button type="submit">Add Post</button>
    </form>
  );
};

export default BlogPostForm;
