import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getDatabase, ref, get } from 'firebase/database';


function FullListing(id) {
  const [listingData, setListingData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const database = getDatabase();
      const snapshot = await get(ref(database, `supplyListings/${id}`));

      if (snapshot.exists()) {
        const data = snapshot.val();
        setListingData(data);
      } else {
        console.log('No data available');
      }
    };
    fetchData();
  }, [id]);

  const contactOwner = () => {
    const ownerEmail = listingData.email;
    window.location.href = `mailto:${ownerEmail}`;
  };

  return (
    <div className="listing-info">
         <p>{listingData.name}</p>
         <p>{listingData.condition}</p>
         <p>{listingData.price}</p>
         <p>{listingData.description}</p>
      <div>
      <p className="listingName">{listingData.name}</p>
      </div>
      <div>
        <img src={listingData.image} alt={`${listingData.name}`}/>
      </div>
      <div>
        <h3>Condition:</h3>
        <p>{listingData.condition}</p>
      </div>
      <div>
        <h3>Description:</h3>
        <p>{listingData.description}</p>
      </div>
      <div>
        <h3>Price:</h3>
        <p> ${listingData.price}</p>
      </div>
      <div>
        {}
        <button className="contact-owner-button" onClick={contactOwner}>
          Contact Owner
        </button>
      </div>
    </div>
  );
};

export default FullListing;
