import React, { useState, useEffect } from 'react';
import Header from './Header';
import Footer from './Footer';
import profileData from './Profile';

function FriendItem({ friend }) {
  return (
    <div className="card m-3" style={{ width: '18rem' }}>
      {/* Render friend details */}
    </div>
  );
}

function Friends() {
  const [searchCriteria, setSearchCriteria] = useState({
    major: '',
    grade: ''
  });
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const storedProfileData = localStorage.getItem('profileData');
    const parsedProfileData = storedProfileData ? JSON.parse(storedProfileData) : [];
    setSearchResults(parsedProfileData);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchCriteria(prevCriteria => ({
      ...prevCriteria,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const filteredProfiles = profileData.filter((profile) => {
      // Filter logic based on search criteria
    });

    setSearchResults(filteredProfiles);
  };

  return (
    <div>
      <Header />
      {/* Your friend search form and results */}
      <Footer />
    </div>
  );
}

export default Friends;
